(function($) {
	$.fn.tableHead = function() {
		var $table = $(this);
		var $thead = $table.find("thead");
		if($table.length==0){
			console.log("未找到Table!");
			return false;
		}
		var table_mark = $table.data("mark");
		if(typeof(table_mark)=="undefined"){
			var timestamp=new Date().getTime();
			table_mark = timestamp;
			$table.data("mark",timestamp);
		}
		var $tableHead;
		if ($(".mark"+table_mark).length > 0) {
			$tableHead = $(".mark"+table_mark).empty();
		}else{
			$tableHead = $("<table class='table-float-head mark"+table_mark+"'></table>");
			var Y = $table.offset().top;
			if($(".tabCont").length>0){
				Y = $(".tabCont").offset().top + $(".tabCont").outerHeight(true);
			}
			$tableHead.css("top" , Y);
			$tableHead.attr("mark","mark"+timestamp);
		}
		$table[0].sortCol='';
		var X = $table.offset().left;
		var H = $thead.outerHeight(true);
		var W = $table.outerWidth(true);
		var $divcss = {
			"left" : X,
			/*"top" : "80px",*/
			"width" : W,
			"height" : H+1,
			"z-index":"999"
		};
		$tableHead.css($divcss);
		var $tr = $("<tr></tr>");
		var totalWidth = 0;
		var sortArr = [];
		if($("#tableSortVal").length>0){
			sortArr = eval($("#tableSortVal").val());
		}else{
			$("body").append('<input type="hidden" id="tableSortVal" value="[]"/>');
		}
		$thead.find('th').each(function(i) {
			if (!$(this).is(":hidden")) {
				var tW = $(this).outerWidth(true);
				var tH = $(this).outerHeight(true);
				var cursor = "default";
				var $th = $("<th class='table-float-th'></th>");
				$th.data("index", i);
				$th.data("name", $(this).attr("name"));
				$th.data("sorttype", $(this).attr("sorttype"));
				$th.data("text",$(this).html());
				if($th.data("sorttype")==0||$th.data("sorttype")==1){
					$th.html($(this).html()+'<i class="i i-arrow-updown"></i>');
					 cursor = "pointer";
				}else{
				$th.html($(this).html());
				}
				var $thcss = {};
				var align = $(this).attr("align");
				/* add by LiuYF 20161101 修复表头多出1~2像素导致换行的问题 */
	        	totalWidth += tW;
	        	if (totalWidth > W - 2) {
	        		tW = tW - (totalWidth - W + 2);
				}
	        	 $thcss = {
						"width" : tW,
						"height" : tH,
						"line-height" : tH + "px",
						"text-align":align,
						"white-space":"nowrap",
						"padding":$(this).css("padding"),
						"cursor":cursor
					};
				$th.css($thcss);
				$th.appendTo($tr);
				$tr.appendTo($tableHead);
				$.each(sortArr,function(i,sortObj){
					if($th.data("name")==sortObj.sortField){
						if(sortObj.sortType=="ASC"){
							$th.addClass("asc");
							$th.html($th.data("text")+'<i class="i i-jiantou5"></i>');
						}else{
							$th.addClass("desc");
							$th.html($th.data("text")+'<i class="i i-jiantou7"></i>');
						}
					}
				});
				if($th.data("sorttype")==0||$th.data("sorttype")==1){
				$th.bind("click", function() {
						var sortObj = {sortField:"",sortType:""};
						sortObj = {sortField:$(this).data("name")};
					$(this).siblings('th').removeClass("desc").removeClass("asc").each(function(){
							if($(this).data("sorttype")==0||$(this).data("sorttype")==1){
								$(this).html($(this).data("text")+'<i class="i i-arrow-updown"></i>');
							}else{
						$(this).html($(this).data("text"));
							}
					});
					if($(this).hasClass("desc")){
							$(this).html($(this).data("text")+'<i class="i i-jiantou5"></i>');
						$(this).removeClass("desc").addClass("asc");
							sortObj.sortType="ASC";
					}else{
							$(this).html($(this).data("text")+'<i class="i i-jiantou7"></i>');
						$(this).removeClass("asc").addClass("desc");
							sortObj.sortType="DESC";
					}
						sortArr = [];
						sortArr.push(sortObj);
						$("#tableSortVal").val(JSON.stringify(sortArr));
						if($(this).data("sorttype")==0){
							sortTable($table,i,"string");
						}else if($(this).data("sorttype")==1){
							/*
							 * 添加了加载动画，没有加在updateTableData方法里面是
							 * 因为入口菜单也有加载动画，否则会出现两个加载图标
							 */
							LoadingAnimate.start();
							updateTableData();
							LoadingAnimate.stop();
						}
				});
				}
				$th=null;
			}
		});
		$tableHead.appendTo('body');
	};
})(jQuery);
//table ID 
var table_id = "#tablist";
var table_class = ".ls_list";
// 显示不同列
function showTable(flag, tId) {
	if (flag) {
		/**
		 * 
		 */
		$(".search-title").find("span").html("").attr("value",$(flag).attr("value"));
		$("li[class=current]").removeClass("current");
		$(flag).addClass("current");
	}
	if (typeof (tId) != "undefined" && tId != null && tId != "") {
		table_class = "." + tId;
	}
	var $tables= $(table_class);
	$tables.each(function(){
		var $table = $(this);
		if (typeof ($(".search-title").find("span").attr("value")) != "undefined") {
			var $flag = $(".search-title").find("span").attr("value").trim();
//			$flag += ",buttonCol";
			var $obj = $table.find("th");
			$obj.each(function() {
				var $index = $(this).index();
				if ($flag.indexOf($(this).attr("name").trim()) > -1&&$(this).attr("coltype")!="hide") {
					$table.find("tr").each(function() {
						$(this).find("td,th").each(function(i) {
							if ($(this).index() == $index) {
								$(this).show();
							}
						});
					});
				} else if($(this).attr("coltype")!="hide"){
					$table.find("tr").each(function() {
						$(this).find("td,th").each(function(i) {
							if ($(this).index() == $index) {
								$(this).hide();
							}
						});
					});
				}
			});
		}
		$table.show();
		$table.tableHead();
	});
}
